mapper.select:
select
  id, customer_user_id, charge_item_id, receivable_amount,ownership_period_begin, ownership_period_end, create_time,due_date, receivble_plan_id, receivble_status, hang_up_status, abrogate_status
from
    tb_charge_manager
    where
    deleted = 0

  and house_id = #{query.houseId}


  and company_id = #{query.companyId}


  and id like concat('%',#{query.receivableCode},'%')


  and receivble_status = #{query.receivbleStatus}


  and hang_up_status = #{query.hangUpStatus}


  and abrogate_status = #{query.abrogateStatus}


  and charge_item_id = #{query.chargeItemId}


  and ownership_period_begin >= #{query.ownershipPeriodBegin}


  and ownership_period_end <= #{query.ownershipPeriodEnd}


  and due_date >= #{query.dueDateBegin}


  and due_date <= #{query.dueDateEnd}
