mapper.select:
SELECT
  b.received_id received_id,b.id receivable_id,b.house_id,charge_item_id,charge_standard,ownership_period_begin,ownership_period_end,a.balance,a.create_time
FROM
  ( SELECT * FROM `tb_house_balance_change_record` WHERE order_name IN ( '账单扣缴', '押金扣缴' ) ) a
    INNER JOIN ( SELECT * FROM tb_securitydeposit_manager WHERE pay_status = 3 ) b ON a.order_id = b.id


      and  b.received_id =#{param.receivedId}


      and  b.id =#{receivableId}


      and  a.create_time >= #{beginDate}


      and  a.create_time <=  DATE_ADD(#{endDate},INTERVAL '1' DAY)


union all

SELECT
  b.id,b.charge_id,b.house_id,charge_item_id, charge_standard,ownership_period_begin,ownership_period_end,a.balance,a.create_time
FROM
  ( SELECT * FROM `tb_house_balance_change_record` WHERE order_name IN ( '账单扣缴', '押金扣缴' ) ) a
    INNER JOIN ( SELECT * FROM tb_charge_received ) b ON a.order_id = b.id



    and  b.id =#{param.receivedId}


    and  b.charge_id =#{receivableId}


    and  a.create_time >= #{beginDate}


    and  a.create_time <=  DATE_ADD(#{endDate},INTERVAL '1' DAY)
