mapper.select:
SELECT o.id, o.status, o.org_name, o.org_key, ug.to_dept_id as dept_id, d.department_name as dept_name, d.path_name as dept_path_name, o.org_type_id, ot.name as org_type_name, u.name as admin_name, u.phone_number, u.username, o.company_name, o.exp_start_date, o.exp_end_date, o.source FROM organization o join user u on u.id = o.org_owner left join user_grant ug on ug.org_id=o.id left join department d on d.id = ug.to_dept_id left join organization_type ot on o.org_type_id=ot.id
AND o.org_type_id = 11 and o.deleted_flag = 0 and u.deleted_flag = 0
AND ug.to_dept_id in
#{item}
AND o.org_name like CONCAT('%',#{orgName},'%')
AND o.status = #{status}
AND o.exp_end_date>=now()
AND o.exp_end_date < now()
AND o.id = #{orgId}
ORDER BY o.id DESC

select id, pk_org_id, change_type, change_name, change_date, change_user_id, change_username, change_person
from organization_change
where pk_org_id=#{orgId}

select id, pk_org_id, change_type, change_name, change_date, change_user_id, change_username, change_person,change_data
from organization_change
where id=#{id}
