mapper.select:
SELECT u.*, uo.pk_org_id, d.id as dept_id, d.department_name as dept_name, d.path_name as dept_path_name, r.id as role_id, r.role_name, r.role_key, p.post_name FROM user u left join user_org uo on uo.pk_user_id = u.id left join organization o on uo.pk_org_id = o.id left join department d on uo.pk_dept_id = d.id left join user_role ur on ur.pk_user_id = u.id left join role r on ur.pk_role_id = r.id left join tb_sys_post p on p.id = u.post_id
AND u.id = #{userId}
AND u.id in
#{userIdItem}
AND u.username like CONCAT('%',#{userName},'%')
AND u.name like CONCAT('%',#{name},'%')
AND u.phone_number = #{phone}
AND u.phone_number = #{phoneNumber}
AND u.`status`= #{status}
AND uo.pk_org_id = #{orgId}
AND uo.pk_dept_id in
#{item}
AND d.path like concat(#{path}, '%')
AND r.id in
#{roleId}
AND u.deleted_flag = 0 AND u.user_type=1 AND o.deleted_flag=0
order by u.created_on desc,u.id desc

SELECT ur.pk_org_id,u.*
FROM user u , user_role ur, role r

    AND ur.pk_user_id = u.id
    AND ur.pk_role_id = r.id

        AND u.username like CONCAT('%',#{userName},'%')


        AND u.name like CONCAT('%',#{name},'%')


        AND u.phone_number = #{phone}


        AND u.phone_number = #{phoneNumber}


        AND u.`status`= #{status}


        AND ur.pk_org_id = #{orgId}


        AND ur.pk_role_id = #{roleId}


        AND r.role_name like CONCAT('%',#{roleNameLike},'%')

    and u.deleted_flag = 0

order by u.created_on desc,u.id desc

mapper.delete:
delete from user_org
WHERE pk_user_id IN

    #{item}

and pk_org_id = #{orgId}

delete from user_role
WHERE pk_user_id IN

    #{item}

and pk_org_id = #{orgId}

mapper.select:
select COUNT(0)
from user_org
where pk_dept_id = #{deptId}

SELECT label_name
FROM label_type
WHERE id = #{labelId}
